import React, { Component } from 'react';
import { Map, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';

import icon from 'leaflet/dist/images/marker-icon.png';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';

let DefaultIcon = L.icon({
    iconUrl: icon,
    shadowUrl: iconShadow
});

L.Marker.prototype.options.icon = DefaultIcon;

class ImovelMaps extends Component {
    constructor() {
        super();
        this.state = {
          lat: -23.5489,
          lng:  -46.6388,
          zoom: 13,
          isHovering: false,
        };
        this.togglePopUp = this.togglePopUp.bind(this);
    }

    toggleHoverState(state) {
        return {
            isHovering: !state.isHovering,
        };
    }

    togglePopUp() {
        this.setState(this.toggleHoverState);
        console.log(this.state.isHovering);
    }

    render() {
      const position = [this.state.lat, this.state.lng]
      const position1 = [-23.5488, -46.6387]
      return (
        <Map center={position} zoom={this.state.zoom}>
          <TileLayer
            attribution="&amp;copy <a href=&quot;http://osm.org/copyright&quot;>OpenStreetMap</a> contributors"
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <Marker position={position} onMouseOver={this.togglePopUp(this)} onMouseOut={this.togglePopUp}>
            {
                this.state.isHovering ?
                        <Popup>
                            <div className="marker-popup">
                                <div className="marker-head">
                                    <h2><b>Condominio</b></h2>
                                    <h3> Rua, Numero </h3>
                                </div>
                                <div className="marker-body">
                                    <p>Logradouro</p>
                                    <p>Condominio</p>
                                    <p>Bairro, Numero</p>
                                </div>
                            </div>
                        </Popup>
                        : ""
            }
          </Marker>
        </Map>
      )
    }
}

export default ImovelMaps;
