import React, { Component } from 'react';
import { Map, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import CSVReader from 'react-csv-reader'
import icon from 'leaflet/dist/images/marker-icon.png';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';

let DefaultIcon = L.icon({
    iconUrl: icon,
    shadowUrl: iconShadow
});

L.Marker.prototype.options.icon = DefaultIcon;

class ImovelMaps extends Component {
    constructor(props) {
        super(props);
        this.state = {
          lat: -23.5489,
          lng:  -46.6388,
          zoom: 13,
          isHovering: false,
        };
        this.togglePopUp = this.togglePopUp.bind(this);
    }

    toggleHoverState(state) {
        return {
            isHovering: !state.isHovering,
        };
    }

    togglePopUp() {
        this.setState(this.toggleHoverState);
        console.log(this.state.isHovering);
        console.log(this.props.imoveisData);
    }

    imoveisError(error) {
        console.log(error);
    }

    imoveisLoaded(csvdata) {
        this.setState({
            imoveis: csvdata
        });
        console.log(this.state.imoveis.length);
    }

    render() {
      const position = [this.state.lat, this.state.lng]
      return (
        <div className="main-container">
            <div className="csv-reader">
                <CSVReader
                    cssClass="csv-input"
                    label="Selecione um arquivo no formato CSV"
                    onFileLoaded={this.imoveisLoaded}
                    onError={this.imoveisError}
                    inputId="csvfile"
                    inputStyle={{color: 'red'}}
                />
            </div>
            <div className="imovel-map-container">
                <Map center={position} zoom={this.state.zoom}>
                    <TileLayer
                        attribution="&amp;copy <a href=&quot;http://osm.org/copyright&quot;>OpenStreetMap</a> contributors"
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    <Marker position={position}>
                            <Popup>
                                <div className="marker-popup">
                                    <div className="marker-head">
                                        <h2><b>Condominio</b></h2>
                                        <h3> Rua, Numero </h3>
                                    </div>
                                    <div className="marker-body">
                                        <p>Logradouro</p>
                                        <p>Condominio</p>
                                        <p>Bairro, Numero</p>
                                    </div>
                                </div>
                            </Popup>
                    </Marker>
                </Map>
            </div>
        </div>
      )
    }
}

export default ImovelMaps;
